// Helper functions
export function getProductById(id: string): Product | undefined {
  return products.find((p) => p.id === id)
}

export function getProductsByCategory(category: string): Product[] {
  return products.filter((p) => p.category === category)
}

export function getAllCategories(): string[] {
  return Array.from(new Set(products.map((p) => p.category)))
}

// Course subcategories helper
export function getCourseSubcategory(product: Product): string {
  if (product.category !== "COURSES") return ""
  
  const title = product.title.toLowerCase()
  
  // Languages
  if (title.includes("chinese") || title.includes("english") || title.includes("french") || 
      title.includes("spanish") || title.includes("hindi") || title.includes("italian") ||
      title.includes("polish") || title.includes("portuguese") || title.includes("russian") ||
      title.includes("language") || title.includes("hsk") || title.includes("learn")) {
    return "Languages"
  }
  
  // Business & Marketing
  if (title.includes("marketing") || title.includes("forex") || title.includes("trading") || 
      title.includes("finance") || title.includes("business") || title.includes("entrepreneur")) {
    return "Business & Marketing"
  }
  
  // Design & Creative
  if (title.includes("canva") || title.includes("drawing") || title.includes("graphic design") || 
      title.includes("design") || title.includes("photoshop") || title.includes("illustrator")) {
    return "Design & Creative"
  }
  
  // Technology & AI
  if (title.includes("chatgpt") || title.includes("ai") || title.includes("office") || 
      title.includes("wordpress") || title.includes("excel") || title.includes("programming") ||
      title.includes("coding") || title.includes("3d printing")) {
    return "Technology & AI"
  }
  
  // Personal Development
  if (title.includes("communication") || title.includes("speaking") || title.includes("confidence") ||
      title.includes("productivity") || title.includes("skills")) {
    return "Personal Development"
  }
  
  return "Other"
}

export function getCoursesBySubcategory(subcategory: string): Product[] {
  return products.filter((p) => p.category === "COURSES" && getCourseSubcategory(p) === subcategory)
}

export function getCourseSubcategories(): string[] {
  const subcategories = products
    .filter(p => p.category === "COURSES")
    .map(p => getCourseSubcategory(p))
  return Array.from(new Set(subcategories)).filter(s => s !== "")
}
